(function(e){function t(t){e(t).css("display","none");e("#modal-bg").fadeOut(200);setTimeout(function(){e("#modal-bg").remove()},200)}e.fn.modal=function(){var n=e("<div id='modal-bg'></div>");e("body").append(n);e("#modal-bg").css({position:"fixed",background:"rgba(0,0,0,0.7)",top:0,left:0,height:"100%",width:"100%","z-index":1e3,opacity:0});e("#modal-bg").fadeTo(200,1);var r=e(this).attr("href");e(r).css({display:"block",position:"fixed",top:0,left:0,opacity:0,opacity:0,"margin-left":(e(window).width()-e(r).outerWidth())/2,"margin-top":(e(window).height()-e(r).outerHeight())/2,"z-index":2e3});e(r).fadeTo(200,1);e("#modal-bg").click(function(){t(r)})};e("a[rel=modal]").click(function(){e(this).modal()});})(jQuery)